const IFRAME = document.querySelector("#Comunica").contentDocument.body
console.log(IFRAME)

document.body.addEventListener("mouseover",(Pointer)=>{
    Pointer.target.classList.add("Clickable")
    console.log("Added")
})

document.addEventListener("mouseout",(Pointer)=>{
    if(Pointer.target.classList.contains("Clickable")){
        console.log(Pointer.target.textContent)
        console.log("removed")
    }
})


document.addEventListener("click",(Pointer)=>{
    if(Pointer.target.classList.contains("Clickable")){
        IFRAME.querySelector("#translateInput").value = Pointer.target.textContent
        IFRAME.querySelector("#translateButton").click()        
    }
    console.log(Pointer.target.textContent)

})